import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { navigateToAuthLogin, getToken, setToken, removeToken, backendUrl } from "./utils";



// Function to refresh tokens
export const refreshTokens = async () => {
  const refreshToken = getToken("refresh_token");
  if (!refreshToken) {
    throw new Error("No refresh token available");
  }

  const response = await fetch(`${backendUrl}/refresh-token`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({ refresh_token: refreshToken }),
  });

  if (response.ok) {
    const data = await response.json();
    setToken("access_token", data.access_token);
    setToken("refresh_token", data.refresh_token);
    return data.access_token;
  } else {
    throw new Error("Failed to refresh token");
  }
};

// Custom fetch function
export const customFetch = async (
  url,
  options = {},
  privateEndpoint = true
) => {
  let accessToken = getToken("access_token");

  const headers = { ...options.headers };
  if (privateEndpoint) {
    headers.Authorization = `Bearer ${accessToken}`;
  }

  const response = await fetch(url, {
    ...options,
    headers,
  });

  if (response.status === 401) {
    // Unauthorized
    try {
      accessToken = await refreshTokens();
      const retryResponse = await fetch(url, {
        ...options,
        headers,
      });
      return retryResponse;
    } catch (error) {
      removeToken("access_token");
      removeToken("refresh_token");
      //   window.location.href = `https://login.microsoftonline.com/your-tenant-id/oauth2/v2.0/authorize?client_id=your-client-id&response_type=code&redirect_uri=http://localhost:3000/redirect&scope=openid profile email offline_access`;
      navigateToAuthLogin();
      return;
    }
  } else if (response.status === 400 || response.status === 500) {
    removeToken("access_token");
    removeToken("refresh_token");
    // window.location.href = `https://login.microsoftonline.com/your-tenant-id/oauth2/v2.0/authorize?client_id=your-client-id&response_type=code&redirect_uri=http://localhost:3000/redirect&scope=openid profile email offline_access`;
    navigateToAuthLogin();
    return;
  }

  return response;
};

// Example component using customFetch
const ExampleComponent = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await customFetch(`${backendUrl}/protected-endpoint`);
        if (response.ok) {
          const data = await response.json();
          console.log("Protected data:", data);
        } else {
          console.error("Failed to fetch protected data");
        }
      } catch (error) {
        console.error("Error:", error);
        navigate("/login");
      }
    };

    fetchData();
  }, [navigate]);

  return <div>Protected Resource</div>;
};

export default ExampleComponent;
