    window.location.href = authUrl;
  };


export const backendUrl =
  "https://bj4458i08a.execute-api.ap-southeast-1.amazonaws.com/test-http-stage"; // "https://bj4458i08a.execute-api.ap-southeast-1.amazonaws.com/test-http-stage" //"https://39y018bqwb.execute-api.ap-southeast-1.amazonaws.com/test" // "https://y9p3kmub24.execute-api.ap-southeast-1.amazonaws.com/test3"  // "https://bj4458i08a.execute-api.ap-southeast-1.amazonaws.com/test-http-stage" //"http://localhost:8000";

// Utility function to get tokens from local storage
export const getToken = (key) => localStorage.getItem(key);

// Utility function to set tokens in local storage
export const setToken = (key, value) => localStorage.setItem(key, value);

// Utility function to remove tokens from local storage
export const removeToken = (key) => localStorage.removeItem(key);
