export const navigateToAuthLogin = async () => {
    // const verifier = generateCodeVerifier();
    // sessionStorage.setItem("code_verifier", verifier);
    // const challenge = await generateCodeChallenge(verifier);


    const authParams = new URLSearchParams({
      response_type: 'code',
      client_id: '8be50f13-27f0-4ad0-88e6-641b6ac13249',
      state: '12345',
      redirect_uri: 'http://localhost:3000/redirect',
      // code_challenge: challenge,
      // code_challenge_method: 'S256',
      // scope: 'openid profile email offline_access',
      scope: 'openid profile email offline_access api://40ffe7a5-3336-4d1d-b74a-4d9d63a54f7c/.default',
      prompt: 'login',
      // response_mode: 'fragment',
    });

    const authUrl = `https://login.microsoftonline.com/61127efd-06a0-469e-aaf8-2e22219ef850/oauth2/v2.0/authorize?${authParams.toString()}`;
    window.location.href = authUrl;
  };
