import React, { useEffect, useState, useRef } from "react";
import {
  BrowserRouter as Router,
  Routes, Route,
  useNavigate,
  useLocation
} from "react-router-dom";

// const backendUrl = "http://ec2-54-179-151-173.ap-southeast-1.compute.amazonaws.com:8000"  // "https://y9p3kmub24.execute-api.ap-southeast-1.amazonaws.com/test" //"http://localhost:8000";

const backendUrl =  "https://y9p3kmub24.execute-api.ap-southeast-1.amazonaws.com/test3"  // "https://bj4458i08a.execute-api.ap-southeast-1.amazonaws.com/test-http-stage" //"https://39y018bqwb.execute-api.ap-southeast-1.amazonaws.com/test" // "https://y9p3kmub24.execute-api.ap-southeast-1.amazonaws.com/test3"  // "https://bj4458i08a.execute-api.ap-southeast-1.amazonaws.com/test-http-stage" //"http://localhost:8000";


const base64UrlEncode = (str) => {
  return btoa(String.fromCharCode.apply(null, new Uint8Array(str)))
    .replace(/\+/g, "-") 
    .replace(/\//g, "_")
    .replace(/=+$/, "");
}

const generateCodeChallenge = async(verifier) => {
  const encoder = new TextEncoder();
  const data = encoder.encode(verifier);
  const digest = await window.crypto.subtle.digest("SHA-256", data);
  return base64UrlEncode(digest);
}

const generateCodeVerifier = () => {
  const array = new Uint32Array(43);
  window.crypto.getRandomValues(array);
  return Array.from(array, (dec) => ("0" + dec.toString(16)).substr(-2)).join("");
}

const Clock = () => {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const timerId = setInterval(() => {
      setTime(new Date());
    }, 1000);

    return () => clearInterval(timerId);
  }, []);

  return (
    <div>
      <h1>{time.toLocaleTimeString()}</h1>
    </div>
  );
};

const navigateToAuthLogin = async () => {
  const verifier = generateCodeVerifier();
  sessionStorage.setItem("code_verifier", verifier);
  const challenge = await generateCodeChallenge(verifier);


  const authParams = new URLSearchParams({
    response_type: 'code',
    client_id: '8be50f13-27f0-4ad0-88e6-641b6ac13249',
    state: '12345',
    redirect_uri: 'http://localhost:3000/redirect',
    code_challenge: challenge,
    code_challenge_method: 'S256',
    scope: 'openid profile email offline_access',
    prompt: 'login',
    response_mode: 'fragment',
  });

  const authUrl = `https://login.microsoftonline.com/61127efd-06a0-469e-aaf8-2e22219ef850/oauth2/v2.0/authorize?${authParams.toString()}`;
  window.location.href = authUrl;
};

const Home = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [apiResponse, setApiResponse] = useState("");

  useEffect(() => {
    const checkSession = async () => {
      try {
        const response = await fetch(`${backendUrl}/check-session`, {
          credentials: "include",
        });
        if (response.ok) {
          setLoggedIn(true);
        } else {
          setLoggedIn(false);
        }
      } catch (error) {
        console.error("Error checking session:", error);
        setLoggedIn(false);
      }
    };

    checkSession();
  }, []);

  const onClick = async () => {
    if (false) {
      try {
        await fetch(`${backendUrl}/logout`, {
          method: "POST",
          credentials: "include",
        });
        setLoggedIn(false);
      } catch (error) {
        console.error("Error logging out:", error);
      }
      return;
    }

    navigateToAuthLogin();
  };

  const apiProtected = async () => {
    try {
      let response = await fetch(`${backendUrl}/protected`, {
        credentials: "include",
      });
      debugger
      if (response.status === 401) {
        navigateToAuthLogin();
      } else {
        const json = await response.json();
        setApiResponse(json);
      }
    } catch (error) {
      console.error("Error fetching protected data:", error);
    }
  };

  const refreshToken = async () => {
    try {
      const response = await fetch(`${backendUrl}/refresh-token`, {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
        },
      });
      const data = await response.json();
      if (data.access_token) {
        setLoggedIn(true);
      } else {
        setLoggedIn(false);
      }
    } catch (error) {
      console.error("Error refreshing token:", error);
      setLoggedIn(false);
    }
  };

  return (
    <>
      <div>
        <h1>Site.</h1>
        <button onClick={onClick}>{loggedIn ? "Sign Out" : "Sign In"}</button>
        <button onClick={apiProtected}>Protected GET URL call</button>
        <button onClick={refreshToken}>Refresh Token</button>
        <button onClick={() => setApiResponse("")}>Clear</button>
      </div>
      <div><Clock /></div>
      <div>Access Token:</div>
      <div>{JSON.stringify(apiResponse)}</div>
    </>
  );
};


const Redirect = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const isRequestSent = useRef(false);

  useEffect(() => {
    // const query = new URLSearchParams(location.search);
    // const code = query.get("code");

    function getQueryParameterByName(name, url = window.location.href) {
      name = name.replace(/[\[\]]/g, '\\$&');
      const regex = new RegExp(`[?&]${name}(=([^&#]*)|&|#|$)`, 'i');
      const results = regex.exec(url);
      if (!results) return null;
      if (!results[2]) return '';
      return decodeURIComponent(results[2].replace(/\+/g, ' '));
    }

    // Example usage
    // const url = "http://example.com/?state=12345&session_state=38b9cc15-ea87-45fd-9fac-276eadb745ec&code=0.Ab4A_X4SYaAGnkaq-C4iIZ74UBMP5YvwJ9BKiOZkG2rBMkm-AF0";
    const code = getQueryParameterByName('code');
    console.log(code); // Outputs the value of the "code" parameter


    const codeVerifier = sessionStorage.getItem("code_verifier");

    debugger

    if (code && codeVerifier && !isRequestSent.current) {
      isRequestSent.current = true;

      fetch("https://login.microsoftonline.com/YOUR_TENANT_ID/oauth2/v2.0/token", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: new URLSearchParams({
          grant_type: 'authorization_code',
          client_id: 'YOUR_CLIENT_ID',
          code: code,
          redirect_uri: 'http://localhost:3000/callback',
          code_verifier: codeVerifier,
        }),      
      })
        .then(response => response.json())
        .then(data => {
          sessionStorage.removeItem("code_verifier");
          console.log("data", data);
          navigate("/");
        })
        .catch(error => {
          console.error("Error:", error);
        });



      // fetch(`${backendUrl}/token`, {
      //   method: "POST",
      //   headers: {
      //     "Content-Type": "application/json",
      //   },
      //   body: JSON.stringify({
      //     redirect_uri: "http://localhost:3000/redirect",
      //     code_verifier: codeVerifier,
      //     code: code,
      //   }),
      //   credentials: "include"
      // })
      //   .then(response => response.json())
      //   .then(data => {
      //     sessionStorage.removeItem("code_verifier");
      //     navigate("/");
      //   })
      //   .catch(error => {
      //     console.error("Error:", error);
      //   });
    }
  }, [location.search]);

  return (
    <div>
      <h1>Handling Redirect...</h1>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/redirect" element={<Redirect />} />
      </Routes>
    </Router>
  );
};

export default App;
